 select DISTINCT a.*
 from t_bill as a
 INNER JOIN  t_bill_price as b on a.id=b.bill_id
INNER  JOIN  t_bill_search_support as d ON a.id=d.bill_id
 INNER  JOIN t_user as e ON b.out_member_id=e.id

 INNER  JOIN  t_bill_group_role as m on a.id=m.t_bill_id
 INNER JOIN t_bill_group as n on m.t_bill_group_id=n.id


   INNER  JOIN t_user_role as f ON b.out_member_id=f.user_id

 where  1=1  AND a.delete_state=0
 AND b.in_member_id=#{userId,jdbcType=BIGINT}
 AND (

   a.state = #{state,jdbcType=INTEGER}


   OR  a.state = #{state2,jdbcType=INTEGER}


   OR  a.state = #{state3,jdbcType=INTEGER}


   OR  a.state = #{state4,jdbcType=INTEGER}


   OR  a.state = #{state5,jdbcType=INTEGER}


   OR  a.state = #{state6,jdbcType=INTEGER}

 )

   AND f.role_id=#{roleId,jdbcType=BIGINT}


   AND a.website LIKE CONCAT('%',#{website,jdbcType=VARCHAR},'%')


   AND a.keywords LIKE CONCAT('%',#{keywords,jdbcType=VARCHAR},'%')


   AND a.create_time LIKE CONCAT('%',#{createTime,jdbcType=TIMESTAMP},'%')


   AND a.update_time = #{updateTime,jdbcType=TIMESTAMP}


   AND  a.create_user_id = #{createUserId,jdbcType=BIGINT}


   AND a.update_user_id = #{updateUserId,jdbcType=BIGINT}


   AND a.first_ranking = #{firstRanking,jdbcType=INTEGER}


   AND  a.new_ranking = #{newRanking,jdbcType=INTEGER}


   AND  a.web_app_id = #{webAppId,jdbcType=INTEGER}


   AND  a.web_app_id1 = #{webAppId1,jdbcType=INTEGER}


   AND  a.standard_days = #{standardDays,jdbcType=INTEGER}


   AND  a.day_optimization = #{dayOptimization,jdbcType=INTEGER}


   AND  a.all_optimization = #{allOptimization,jdbcType=INTEGER}


   AND  a.bill_ascription= #{billAscription,jdbcType=BIGINT}


   AND  d.search_support= #{searchName,jdbcType=VARCHAR}


   AND  b.out_member_id= #{searchUserNameId,jdbcType=VARCHAR}


   AND a.new_ranking <= b.bill_ranking_standard


   AND  a.opstate = #{opstate,jdbcType=INTEGER}


   AND  a.bill_type = #{billType,jdbcType=INTEGER}


   AND  a.apply_state= #{applyState,jdbcType=INTEGER}


   AND  a.first_ranking >= #{firstRanking1,jdbcType=INTEGER}


   AND  a.first_ranking <= #{firstRanking2,jdbcType=INTEGER}


   AND  a.new_ranking >= #{newRanking1,jdbcType=INTEGER}


   AND  a.new_ranking <= #{newRanking2,jdbcType=INTEGER}


   AND  a.change_ranking >= #{newchange1,jdbcType=INTEGER}


   AND  a.change_ranking <= #{newchange2,jdbcType=INTEGER}


   AND  DATE_FORMAT(a.create_time,'%Y-%m-%d')  >= DATE_FORMAT( #{addTime1,jdbcType=VARCHAR},'%Y-%m-%d')


   AND  DATE_FORMAT(a.create_time,'%Y-%m-%d')  <= DATE_FORMAT( #{addTime2,jdbcType=VARCHAR},'%Y-%m-%d')


   AND  a.standard_days >= #{standardDays1,jdbcType=INTEGER}


   AND  a.standard_days <= #{standardDays2,jdbcType=INTEGER}


   AND   n.t_user_id=#{groupUserId,jdbcType=BIGINT} and n.id=#{groupId,jdbcType=BIGINT}



     ORDER BY e.user_name ${sortOrder}


     ORDER BY a.keywords ${sortOrder}


     ORDER BY a.website ${sortOrder}


     ORDER BY d.search_support ${sortOrder}


     ORDER BY a.create_time ${sortOrder}


     ORDER BY a.first_ranking ${sortOrder}


     ORDER BY a.new_ranking ${sortOrder}


     ORDER BY a.change_ranking ${sortOrder}


     ORDER BY a.standard_days ${sortOrder}


     ORDER BY a.state ${sortOrder}


     ORDER BY a.opstate ${sortOrder}


     ORDER BY a.day_optimization ${sortOrder}


     ORDER BY a.all_optimization ${sortOrder}



   limit #{offset},#{limit}

