    ORDER BY a.website ${sortOrder}


    ORDER BY d.search_support ${sortOrder}


    ORDER BY a.create_time ${sortOrder}


    ORDER BY a.first_ranking ${sortOrder}


    ORDER BY a.new_ranking ${sortOrder}


    ORDER BY a.change_ranking ${sortOrder}


    ORDER BY a.standard_days ${sortOrder}


    ORDER BY a.state ${sortOrder}


limit #{offset},#{limit}

select count(a.id)
from t_bill as a,t_bill_search_support as d,t_user as e
where a.id=d.bill_id and a.create_user_id=e.id  AND a.delete_state=0
AND e.create_user_id = #{userId,jdbcType=BIGINT}
AND (

  a.state = #{state,jdbcType=INTEGER}


  OR  a.state = #{state2,jdbcType=INTEGER}

)

  AND a.website LIKE CONCAT('%',#{website,jdbcType=VARCHAR},'%')


  AND a.keywords LIKE CONCAT('%',#{keywords,jdbcType=VARCHAR},'%')


  AND a.create_time = #{createTime,jdbcType=TIMESTAMP}


  AND a.update_time = #{updateTime,jdbcType=TIMESTAMP}


  AND  a.create_user_id = #{createUserId,jdbcType=BIGINT}


  AND a.update_user_id = #{updateUserId,jdbcType=BIGINT}


  AND a.first_ranking = #{firstRanking,jdbcType=INTEGER}


  AND  a.new_ranking = #{newRanking,jdbcType=INTEGER}


  AND  a.web_app_id = #{webAppId,jdbcType=INTEGER}


  AND  web_app_id1 = #{webAppId1,jdbcType=INTEGER}


  AND  a.standard_days = #{standardDays,jdbcType=INTEGER}


  AND  a.day_optimization = #{dayOptimization,jdbcType=INTEGER}


  AND  a.all_optimization = #{allOptimization,jdbcType=INTEGER}


  AND  a.opstate = #{opstate,jdbcType=INTEGER}


  AND  a.bill_type = #{billType,jdbcType=INTEGER}


  AND  a.bill_ascription= #{billAscription,jdbcType=BIGINT}


  AND  a.apply_state= #{applyState,jdbcType=INTEGER}


  AND  d.search_support= #{searchName,jdbcType=VARCHAR}


  AND a.new_ranking <= b.bill_ranking_standard



    ORDER BY e.user_name ${sortOrder}


    ORDER BY a.keywords ${sortOrder}


    ORDER BY a.website ${sortOrder}


    ORDER BY d.search_support ${sortOrder}


