  AND  web_app_id1 = #{webAppId1,jdbcType=INTEGER}


   AND  standard_days = #{standardDays,jdbcType=INTEGER}


  AND  day_optimization = #{dayOptimization,jdbcType=INTEGER}


  AND  all_optimization = #{allOptimization,jdbcType=INTEGER}


