mapper.select:
select

from orderlease
where id = #{id,jdbcType=BIGINT}

select COUNT(*) from ( select website from orderlease where receiveId = #{receiveId,jdbcType=BIGINT}
and (reserveId=#{reserveId,jdbcType=BIGINT} or orderstate=2 or orderstate=3)
group by website) aa

select id,website,createTime as time,orderstate,receiveId,reserveId from orderlease where receiveId = #{receiveId,jdbcType=BIGINT}
and (reserveId=#{reserveId,jdbcType=BIGINT} or orderstate=2 or orderstate=3)
and orderstate > 1 group by website order by id DESC LIMIT #{offset},#{limit}

select *
from orderlease
where website = #{website,jdbcType=VARCHAR}

select *
from orderlease
where orderId = #{OrderId,jdbcType=VARCHAR}
